// MistCanvas.jsx
import React, { useRef, useEffect, useCallback } from 'react';

const MistCanvas = ({ width, height }) => {
  const canvasRef = useRef(null);

  // Wrap `draw` function with `useCallback` to prevent it from being recreated on every render
  // Also, make sure to pass an empty dependency array since there are no dependencies
  const draw = useCallback((ctx, mouseX, mouseY) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';

    // Drawing the mist with dispersion effect
    for (let i = 0; i < 10; i++) {
      let x = Math.random() * width;
      let y = Math.random() * height;

      if (Math.abs(x - mouseX) > 50 && Math.abs(y - mouseY) > 50) {
        ctx.beginPath();
        ctx.arc(x, y, 30, 0, Math.PI * 2);
        ctx.fill();
      }
    }
  }, [width, height]); // If width and height change, we want to recreate this method

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    // Store the reference to the `draw` function so it can be removed later
    const mouseMoveHandler = (event) => {
      draw(context, event.clientX, event.clientY);
    };

    // Add the mousemove event listener to window
    window.addEventListener('mousemove', mouseMoveHandler);

    // Return a cleanup function to remove the event listener
    return () => {
      window.removeEventListener('mousemove', mouseMoveHandler);
    };
  }, [draw]); // `draw` is a dependency now, and we are certain it won't change unnecessarily

  return <canvas ref={canvasRef} width={width} height={height} />;
};

export default MistCanvas;
