import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './Question.css';

function QuestionInput() {
  const [question, setQuestion] = useState('');
  const history = useHistory(); // Hook to give you access to the history instance

  const handleSubmit = () => {
    // You can send the question to your backend here
    history.push('/interview'); // This is where you define the path for your interview app component
  };

  return (
    <div className="question-container">
      <h2 className="question-prompt">What question are you going to answer today?</h2>
      <input
        type="text"
        className="question-input"
        placeholder="Enter your question here"
        value={question}
        onChange={e => setQuestion(e.target.value)}
      />
      <button onClick={handleSubmit} className="submit-button">Submit</button>
    </div>
  );
}

export default QuestionInput;
