// MistCanvas.jsx
import React, { useRef, useEffect } from 'react';

const MistCanvas = ({ width, height }) => {
  const canvasRef = useRef(null);

  // This function is responsible for drawing the mist
  const draw = (ctx, mouseX, mouseY) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';

    // Example: Draw some shapes for the mist
    for (let i = 0; i < 10; i++) {
      let x = Math.random() * width;
      let y = Math.random() * height;

      // Simple dispersion effect by avoiding drawing near the mouse
      if (Math.abs(x - mouseX) > 50 && Math.abs(y - mouseY) > 50) {
        ctx.beginPath();
        ctx.arc(x, y, 30, 0, Math.PI * 2);
        ctx.fill();
      }
    }
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    // Event listener for mouse movement
    const mouseMoveHandler = (event) => {
      draw(context, event.clientX, event.clientY);
    };

    window.addEventListener('mousemove', mouseMoveHandler);

    return () => {
      window.removeEventListener('mousemove', mouseMoveHandler);
    };
  }, [draw]);

  return <canvas ref={canvasRef} width={width} height={height} />;
};

export default MistCanvas;
